css:
busy-signal {
  position: relative;
  width: 16px;
  height: 100%;
}
busy-signal.idle::before,
busy-signal.busy::before {
  content: '';
  position: absolute;
  width: 6px;
  height: 6px;
  top: 50%;
  left: 5px;
  margin-top: -3px;
  border-radius: 50%;
  transition: background .5s linear;
}
busy-signal.idle::before {
  background: #52fa7c;
}
busy-signal.busy {
  cursor: wait;
}
busy-signal.busy::before {
  background-color: #bf95f9;
  animation: bounce 1s infinite ease-in-out;
}
@keyframes bounce {
  0%,
  100% {
    transform: scale(1);
  }
  50% {
    transform: scale(0.6);
  }
}
